ini_set('display_errors', 1);
set_time_limit(0);
		
class Tiler
{
	public function makeTiles()
	{
		$file = __DIR__ . '/maps/imp1.png';
		$img = imagecreatefrompng($file);
		$tiles = array();
		$info = getimagesize($file);
		
		$map_hashes = array();
		
		$debug = microtime(true);
		for ($x=0; $x<$info[0]; $x += 24)
		{
			for ($y=0; $y<$info[1]; $y += 24)
			{
				$tile = imagecreatetruecolor(24, 24);
				imagecopy($tile, $img, 0, 0, $x, $y, 24, 24);
				
				//Calculate hash
				$hash = 0;
				for ($px = 0; $px<24; ++$px)
					for ($py = 0; $py<24; ++$py)
					{
						$rgb = imagecolorat($tile, $px, $py);
						$hash = $hash ^ $rgb;
					}
					
				$map_hashes[$x/24][$y/24] = $hash;
					
				if (isset($tiles[$hash]))
					imagedestroy($tile);
				else
					$tiles[$hash] = $tile;
			}
		}
		imagedestroy($img);
		
		$hash_to_index = array();
		
		$cnt = count($tiles);
		$img_size = ceil(sqrt($cnt));
		$img = imagecreatetruecolor(24*$img_size, 24*$img_size);
		$i = 0;
		foreach ($tiles as $hash => &$tile)
		{
			$hash_to_index[$hash] = $i;
			imagecopy($img, $tile, ($i%$img_size)*24, floor($i/$img_size)*24, 0, 0, 24, 24);
			imagedestroy($tile);
			++$i;
		}
		imagepng($img, __DIR__ . '/maps/2.png');
		imagedestroy($img);
		
		//Build map array
		for ($x=0; $x<$info[0]; $x += 24)
		{
			for ($y=0; $y<$info[1]; $y += 24)
			{
				$map_hashes[$x/24][$y/24] = $hash_to_index[$map_hashes[$x/24][$y/24]];
			}
		}
		
		echo 'Tiles found: '.$cnt.'<br>';
		echo 'Time: '.(microtime(true) - $debug);
		
		echo '<br><br>'.json_encode($map_hashes);
	}
	
	public function buildImage($data)
	{
		$file = __DIR__ . '/maps/imp1.png';
		$data = json_decode($data);
		
		$final_width = count($data);
		$final_height = count($data[0]);
		
		$debug = microtime(true);
		
		//Create tiles
		$img = imagecreatefrompng($file);
		$tiles = array();
		$info = getimagesize($file);
		$i = 0;
		for ($y=0; $y<$info[1]; $y += 24)
			for ($x=0; $x<$info[0]; $x += 24)
			{
				$tiles[$i] = imagecreatetruecolor(24, 24);
				imagecopy($tiles[$i], $img, 0, 0, $x, $y, 24, 24);
				++$i;
			}
		imagedestroy($img);
		
		$img = imagecreatetruecolor($final_width*24, $final_height*24);
		foreach ($data as $x => $data_y)
			foreach ($data_y as $y => $tile_index)
				imagecopy($img, $tiles[$tile_index], $x*24, $y*24, 0, 0, 24, 24);
		
		imagepng($img, __DIR__ . '/maps/imp1-full.png');
		imagedestroy($img);
		
		echo 'Time: '.(microtime(true) - $debug);
	}
}

